    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // buscar:
        ControleFornecedor cf = new ControleFornecedor();
        int i = Integer.parseInt(jTextField1.getText());
        Fornecedor forn = cf.getPorId(i);
        if (forn != null) {
            jTextField2.setText(forn.getNome());
            jTextField3.setText(forn.getFone());
            jButton3.setEnabled(true);
            jButton4.setEnabled(true);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // inserir:
        String nome = jTextField2.getText();
        String fone = jTextField3.getText();
        ControleFornecedor cntf = new ControleFornecedor();
        cntf.inserir(nome, fone);
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField1.requestFocus();
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // altera:
        int i = Integer.parseInt(jTextField1.getText());
        String nome = jTextField2.getText();
        String fone = jTextField3.getText();
        ControleFornecedor cntf = new ControleFornecedor();
        cntf.alterar(i, nome, fone);
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField1.requestFocus();
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // excluir:
        int i = Integer.parseInt(jTextField1.getText());
        String nome = jTextField2.getText();
        String fone = jTextField3.getText();
        ControleFornecedor cntf = new ControleFornecedor();
        cntf.excluir(i, nome, fone);               
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField1.requestFocus();
    }//GEN-LAST:event_jButton4ActionPerformed

